:root {
    --font-family: sans-serif;
    font-size: 16px;
    --color-green: #06c167;
    --color-grey: rgb(238, 238, 238);
    --color-greyDarker: rgb(84, 84, 84);
  }
  
  * {
    box-sizing: border-box;
  }
  
  .smallText {
    font-size: 0.7rem;
    font-weight: 300;
  }
  
  body {
    margin: 0;
    font-family: sans-serif;
  }
  
  .container {
    width: calc(100% - 120px);
    margin: auto;
  }
  
  header {
    border-bottom: 1px solid #ddd;


    .logo {
        cursor: pointer;
      h1 {
        font-weight: 300;
        span {
          font-weight: 600;
          color: var(--color-green);
        }
      }
    }
    .container {
      display: flex;
      align-items: center;
    }
    .searchBar {
      margin-left: auto;
    }
    .header-option {
      padding-left: 5rem;
      letter-spacing: 0.6px;
      display: flex;
      align-items: center;
      cursor: pointer;
      svg {
        margin-right: 1rem;
        display: inline-flex;
      }
    }
    .currentDetails,
    .searchBar {
      display: flex;
    }
  }
  
  .options {
    padding: 1.2rem 0;
    .options-btn {
      background: var(--color-grey);
      padding: 0.6rem 0.8rem;
      border-radius: 30px;
      border: 0;
      font-family: var(--font-family);
      font-size: 1rem;
      letter-spacing: 0.6px;
      display: inline-flex;
      align-items: center;
      margin-right: 0.3rem;
      svg {
        max-width: 20px;
        margin-right: 0.4rem;
      }
      &.selected {
        background: black;
        color: white;
      }
    }
  }
  
  .listings {
    padding: 2rem 0;
    .header {
      display: flex;
      align-items: center;
      .header-title {
        h2 {
          font-weight: 400;
          font-size: 1.7rem;
          margin-bottom: 0.5rem;
          margin-top: 0;
        }
        span {
          color: var(--color-greyDarker);
          font-weight: 300;
          font-size: 0.9rem;
        }
      }
      .header-viewOptions {
        display: flex;
        align-items: center;
        margin: auto 0 auto auto;
        .viewAll {
          color: var(--color-green);
          padding-right: 2rem;
          cursor: pointer;

        }
        .viewMore {
          .arrow {
            background: red;
            align-items: center;
            justify-content: center;
            background: var(--color-grey);
            opacity: 0.3;
            transition: all 0.2s ease-in-out;
            cursor: pointer;
            &.darker,
            &:hover {
              opacity: 1;
            }
          }
        }
      }
    }
    .circle {
      width: 40px;
      height: 40px;
      align-items: center;
      justify-content: center;
      border-radius: 50%;
      display: inline-flex;
    }
    .listings-grid {
      display: flex;
      overflow: hidden;
      overflow-x: scroll;
      scroll-snap-type: x mandatory;
      scroll-behavior: smooth;
  
      &::-webkit-scrollbar {
        display: none;
      }
  
      .listings-col {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: space-between;
        flex-grow: 0;
        flex-shrink: 0;
        flex-basis: 100%;
        scroll-snap-align: start;
        margin-left: 25px;
      }
  
      .listings-grid-element {
        width: calc(1 / 3 * 100% - (1 - 1 / 3) * 25px);
        margin-top: 2rem;
  
        .image {
          height: 240px;
          overflow: hidden;
          img {
            width: 100%;
            height: 100%;
            object-fit: cover;
          }
        }
        .text-lower {
          padding: 0.6rem 0;
          span {
            color: var(--color-greyDarker);
          }
        }
        .text {
          display: flex;
          align-items: center;
          border-bottom: 1px solid #ddd;
          padding: 1rem 0;
          h3 {
            font-weight: 400;
            margin-bottom: 0.3rem;
            margin-top: 0;
          }
          span {
            font-size: 0.8rem;
            font-weight: 300;
          }
          .rating {
            margin: auto 0 auto auto;
            height: 100%;
  
            span {
              height: 40px;
              width: 40px;
              background: var(--color-grey);
              font-size: 1rem;
              color: black;
            }
          }
        }
      }
    }
  }
